import React from 'react';
import { useParams } from 'react-router-dom';
import './EditPost.css'
import { supabase } from '../client';

const EditPost = () => {

    const {id} = useParams();
    const showPost = () => {
        supabase.from('Posts').select().eq('id', id);
    }    
    const post = showPost()[0];
    const updatePost = async(event) => {
        event.preventDefault();
        const name = document.getElementById('name').value;
        const speed = document.getElementById('speed').value;
        const color = document.getElementById('color').value;
        await supabase.from('Posts')
                      .update({ name: name, speed: speed,  color: color})
                      .eq('id', id);
        window.location = "/";
    }

    return (
        <div>
            <form>
                <label for="title">Title</label> <br />
                <input type="text" id="title" name="title" value={post.title} /><br />
                <br/>

                <label for="author">Author</label><br />
                <input type="text" id="author" name="author" value={post.author} /><br />
                <br/>

                <label for="description">Description</label><br />
                <textarea rows="5" cols="50" id="description" value={post.description} >
                </textarea>
                <br/>
                <input type="submit" value="Submit" onClick={updatePost} />
                <button className="deleteButton">Delete</button>
            </form>
        </div>
    )
}

export default EditPost
